import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  name: 'LoginPage',
  data() {
    return {
      email: '',
      password: ''
    };
  },
  methods: {
    async login() {
      // Créer un objet contenant les données d'authentification
      const credentials = {
        email: this.email,
        password: this.password
      };
      try {
        // Envoyer les données d'authentification au serveur JSON (dans ce cas, on suppose que le chemin d'accès est correct)
        const response = await axios.get('http://localhost:3000/user?email=' + credentials.email);

        // Vérifiez si un utilisateur avec cet e-mail a été trouvé
        if (response.data.length === 1) {
          const user = response.data[0];
          localStorage.setItem('user-info', JSON.stringify(credentials.email));

          // Vérifiez si le mot de passe correspond
          if (user.password === credentials.password) {
            // Authentification réussie, redirigez l'utilisateur vers la page de succès
            localStorage.setItem('user-info', JSON.stringify(credentials.password));
            this.$router.push({
              name: 'HomePage'
            });
          } else {
            // Mot de passe incorrect, affichez un message d'erreur
            console.error('Mot de passe incorrect');
          }
        } else {
          // Aucun utilisateur avec cet e-mail trouvé, affichez un message d'erreur
          console.error('Utilisateur non trouvé');
        }
      } catch (error) {
        // Gérez les erreurs ici
        console.error('Échec de l\'authentification:', error);
        alert('Échec de l\'authentification');
      }
    }
  }
};
